 * 
 * 
 * 
 * 
 */


import {useState, useEffect} from 'react';

import Card from '../components/Card.js'
import tStyle from './style/Todo.module.css';


function Todo() {
    var cards = new Object();
    const [, rerender] = useState();
    const [key, setKey] = useState(1);
    var cards = {
        0: {
            name: "",
            todoList: {
                0: "",
            }
        }
    }



    const [ident, setIdent] = useState(1);
    const [removeState, setRemoveState] = useState(false);
    // let [cards, setCards] = useState([
    //                                         {
    //                                             id: 0,
    //                                             name: "",
    //                                             description: "",
    //                                             todo: [""]
    //                                         },

    //                                     ]);




    const addCard = () => {
        setRemoveState(false);
        cards[key] = {
            0: {
                name: "",
                todoList: {
                    0: "",
                }
            }
        };


        setKey(prev => prev + 1);
    }

    const removeCard = (cardKey) => { 
        console.log("Todo removeCard cardkey: ", cardKey);
        if (removeState) 
        {
            console.log("Todo removeCard cards b4: ", cards);

            delete cards[cardKey];
            console.log("Todo removeCard cards after: ", cards);
            rerender();
            console.log("Todo.js cards length: ", cards.length);
        }
    }

    useEffect( () => {


    }, [])

    return (
        <div className={tStyle.todoContainer}>
            <div className={tStyle.editsContainer}>
                <button className={tStyle.addEvent}
                        onClick={addCard}>
                    ＋
                </button>
                <button className={tStyle.removeEvent}
                        onClick={() => {console.log(removeState); setRemoveState(!removeState); console.log(removeState)}}>
                    －
                </button>
            </div>

            <div className={tStyle.cardsContainer}> 
                {

                    Object.keys(cards).map(cardKey => {
                        console.log("Todo.js Key: ", key);
                        return (
                            <Card removeCard = {removeCard}
                                  removeState = {removeState} 
                                  card = {cards[cardKey]}
                                  cardKey = {cardKey}></Card>
                        )
                    })
                }


            </div>

        </div>
    );
}

export default Todo;
