/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * 
 * TodoItem.js
 * 
 * 
 * 
 * 
 */


import { useState, useEffect, useRef} from 'react';
import { TextInput } from 'react-native';
import { isCompositeComponent } from 'react-dom/test-utils';
import { ClickAwayListener } from '@material-ui/core';



import tStyle from './style/TodoItem.module.css';


function Card(props) {

    const {todoItemKey, deleteTodoItem, updateTodoItemMessage, todo, key} = props;
    // const [inputEdited, setInputEdited] = useState(false);
    let inputEdited = false;
    var message = todo;
    const deleteCurrent = () => 
    {
        deleteTodoItem(todoItemKey);


    }

    const updateMessage = (target) =>
    {        
        // console.log("TodoItem updateMessage target: " , target);
        // console.log("TodoItem updateMessage target: " , target.value);
        // console.log("TodoItem updateMessage mess: " , message);
        updateTodoItemMessage(todoItemKey, target.value);
        // console.log("TodoItem updateMessage message: " , elem.target.value);
    }

    useEffect(() =>
    {
        const todoInput = document.getElementById(key);


        function checkOutside(e) 
        {
            console.log("todoItem checkOutside called: ", inputEdited);
            var clickedInsideInput = todoInput.contains(e.target);
            if (inputEdited && !clickedInsideInput) {
                // console.log("TodoItem oh shet clicked outside");
                updateMessage(todoInput);
                inputEdited = false;
            }
        }

        document.addEventListener("click", (e) => checkOutside(e));

        return (() => 
        {
            document.removeEventListener("click", (e) => checkOutside(e));
        })

    },[])



    return (
        <div className={tStyle.todoItemContainer}>
            <input type="text" 
                   placeholder="what to do... " 
                   defaultValue={message} 
                   id = {key}
                   className={tStyle.messageInput}
                   onChange={(e) => {inputEdited = true; console.log("textinput changed");}}
                   />

            <div className={tStyle.deleteContainer} onClick={deleteCurrent}>âœ˜</div>
        </div>
    );
}

export default Card;
