import Card from '../components/Card.js'
import tStyle from './style/Todo.module.css';


function Todo() {
    var car


    const [ident, setIdent] = useState(1);
    const [removeState, setRemoveState] = useState(false);
    let [cards, setCards] = useState([
                                            {
                                                id: 0,
                                                name: "",
                                                description: "",
                                                todo: [""]
