 * Card.js
 * 
 * 
 * 
 * 
 */


import {useState, useEffect} from 'react';
import { isCompositeComponent } from 'react-dom/test-utils';
import ClickAwayListener from '@material-ui/core/ClickAwayListener';


import cStyle from './style/Card.module.css';


function Card(props) {

    const [addState, setAddState] = useState(false);
    const [textElem, setTextElem] = useState(null);
    const [clicked, setClicked] = useState(false);
    const [cards, setCards] = useState([
                                            {

                                            }
                                        ]);

    /* cards: 
        {
            name: card x
            description: 

        } */


    const changeText = (e) => {

        console.log("Card.js changeText " , e)
        // target.innerHTML = <div>heu</div>
        setClicked(false);
    }

    const switchColor = (elem) => {
        if (!clicked) {
            console.log("fired once");
            if (elem.target.style.backgroundColor == "rgb(158, 205, 73)") {
                elem.target.style.backgroundColor = "#ffffff";
                elem.target.style.color = "#666668";
            }
            else {
                elem.target.style.backgroundColor = "#9ecd49";
                elem.target.style.color = "#ffffff";
                elem.target.style.border = "#9ecd49 2px solid"

            }
        }
        console.log(elem.detail);
        if(elem.detail == 2) {

            setClicked(false);
        }

    }

    return (
        <div className={cStyle.cardContainer}>
            <div className={cStyle.cardName}
                 dangerouslySetInnerHTML={{__html: props.card.name}}>
            </div>
            <div className={cStyle.cardDescription}
                 dangerouslySetInnerHTML={{__html: props.card.description}}>
            </div>
            <br/>
            {
                props.card.todo.map((deets) => {
                    return (
                        <div id={deets} className={cStyle.cardDetails} 
                             onDoubleClick={(e) => {setClicked(true); changeText(e); }}
                             onClick={(e) => {switchColor(e); console.log("clicked? ", clicked);}}
                             dangerouslySetInnerHTML={{__html: deets}}>
                        </div>
                    );
                })
            }


        </div>
    );
}

export default Card;
